const axios = require("axios");
const cheerio = require("cheerio");

module.exports = (async (query) => {
  let { data } = await axios(
    `https://id.pinterest.com/search/pins/?q=${encodeURIComponent(
      query
    )}&autologin=true`,
    {
      headers: {
        cookie:
          '_auth=1; _b="AVna7S1p7l1C5I9u0+nR3YzijpvXOPc6d09SyCzO+DcwpersQH36SmGiYfymBKhZcGg="; _pinterest_; _ir=0',
      },
    }
  );
  $ = cheerio.load(data);
  return $("div > img")
    .get()
    .filter((a) => a.attribs.src && !/75x75_RS/.test(a.attribs.src))
    .map(
      (a) =>
        "https://i.pinimg.com/originals/" +
        a.attribs.src.split("/").slice(4).join("/")
    );
}).bind();
